import React, { useEffect, useRef } from 'react';
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Dimensions,
  Animated,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import * as Haptics from 'expo-haptics';

const { width, height } = Dimensions.get('window');

export default function HomeScreen({ onStartGame, score, highScore }) {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.8)).current;
  const pulseAnim = useRef(new Animated.Value(1)).current;

  useEffect(() => {
    // Entrance animation
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.spring(scaleAnim, {
        toValue: 1,
        tension: 50,
        friction: 7,
        useNativeDriver: true,
      }),
    ]).start();

    // Pulse animation for play button
    const pulseAnimation = Animated.loop(
      Animated.sequence([
        Animated.timing(pulseAnim, {
          toValue: 1.05,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(pulseAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
      ])
    );
    pulseAnimation.start();

    return () => pulseAnimation.stop();
  }, []);

  const handlePlayPress = () => {
    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Heavy);
    onStartGame();
  };

  return (
    <LinearGradient
      colors={['#667eea', '#764ba2', '#f093fb']}
      style={styles.container}
    >
      <Animated.View 
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ scale: scaleAnim }],
          },
        ]}
      >
        {/* Game Title */}
        <View style={styles.titleContainer}>
          <Text style={styles.title}>TAP</Text>
          <Text style={styles.subtitle}>MASTER</Text>
          <View style={styles.titleUnderline} />
        </View>

        {/* Score Display */}
        <View style={styles.scoreContainer}>
          {score > 0 && (
            <View style={styles.scoreBox}>
              <Text style={styles.scoreLabel}>Last Score</Text>
              <Text style={styles.scoreValue}>{score}</Text>
            </View>
          )}
          {highScore > 0 && (
            <View style={styles.scoreBox}>
              <Text style={styles.scoreLabel}>Best Score</Text>
              <Text style={styles.scoreValue}>{highScore}</Text>
            </View>
          )}
        </View>

        {/* Play Button */}
        <Animated.View style={{ transform: [{ scale: pulseAnim }] }}>
          <TouchableOpacity
            style={styles.playButton}
            onPress={handlePlayPress}
            activeOpacity={0.8}
          >
            <LinearGradient
              colors={['#ff6b6b', '#ee5a24']}
              style={styles.playButtonGradient}
            >
              <Text style={styles.playButtonText}>PLAY</Text>
            </LinearGradient>
          </TouchableOpacity>
        </Animated.View>

        {/* Game Description */}
        <Text style={styles.description}>
          Tap the circles as fast as you can!{'\n'}
          Test your reflexes and beat your high score.
        </Text>

        {/* Floating Elements */}
        <View style={styles.floatingElements}>
          {[...Array(6)].map((_, index) => (
            <FloatingCircle key={index} delay={index * 500} />
          ))}
        </View>
      </Animated.View>
    </LinearGradient>
  );
}

const FloatingCircle = ({ delay }) => {
  const animValue = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    const animation = Animated.loop(
      Animated.sequence([
        Animated.timing(animValue, {
          toValue: 1,
          duration: 3000,
          useNativeDriver: true,
        }),
        Animated.timing(animValue, {
          toValue: 0,
          duration: 3000,
          useNativeDriver: true,
        }),
      ])
    );

    setTimeout(() => animation.start(), delay);

    return () => animation.stop();
  }, [delay]);

  const translateY = animValue.interpolate({
    inputRange: [0, 1],
    outputRange: [0, -50],
  });

  const opacity = animValue.interpolate({
    inputRange: [0, 0.5, 1],
    outputRange: [0.3, 0.8, 0.3],
  });

  return (
    <Animated.View
      style={[
        styles.floatingCircle,
        {
          transform: [{ translateY }],
          opacity,
          left: Math.random() * (width - 20),
          top: Math.random() * (height - 100),
        },
      ]}
    />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 30,
  },
  titleContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  title: {
    fontSize: 64,
    fontWeight: '900',
    color: '#fff',
    textShadowColor: 'rgba(0, 0, 0, 0.3)',
    textShadowOffset: { width: 2, height: 2 },
    textShadowRadius: 4,
    letterSpacing: 4,
  },
  subtitle: {
    fontSize: 32,
    fontWeight: '600',
    color: '#fff',
    opacity: 0.9,
    letterSpacing: 8,
    marginTop: -5,
  },
  titleUnderline: {
    width: 100,
    height: 3,
    backgroundColor: '#fff',
    marginTop: 10,
    borderRadius: 2,
  },
  scoreContainer: {
    flexDirection: 'row',
    marginBottom: 50,
    gap: 20,
  },
  scoreBox: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    paddingHorizontal: 20,
    paddingVertical: 15,
    borderRadius: 15,
    alignItems: 'center',
    backdropFilter: 'blur(10px)',
  },
  scoreLabel: {
    color: '#fff',
    fontSize: 14,
    fontWeight: '500',
    opacity: 0.8,
  },
  scoreValue: {
    color: '#fff',
    fontSize: 24,
    fontWeight: '700',
    marginTop: 5,
  },
  playButton: {
    marginBottom: 40,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 8 },
    shadowOpacity: 0.3,
    shadowRadius: 12,
    elevation: 8,
  },
  playButtonGradient: {
    paddingHorizontal: 60,
    paddingVertical: 20,
    borderRadius: 30,
    alignItems: 'center',
  },
  playButtonText: {
    color: '#fff',
    fontSize: 24,
    fontWeight: '800',
    letterSpacing: 2,
  },
  description: {
    color: '#fff',
    fontSize: 16,
    textAlign: 'center',
    opacity: 0.8,
    lineHeight: 24,
    fontWeight: '400',
  },
  floatingElements: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  floatingCircle: {
    position: 'absolute',
    width: 20,
    height: 20,
    borderRadius: 10,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
  },
});
